import React, { Component } from 'react';
import CheckboxList from '../../CheckboxList/CheckboxList.js';

class HospitalTypeFilter extends Component {

    state = {
        mappingObject : {},
        selectedValues : []
    }


    checkboxSelectItem = (item) => {

        // removes item if in selectedValues
        let values = this.state.selectedValues.filter((value) => {
            return (value !== item)
        });

        // adds item if not in selectedValues
        if (values.length === this.state.selectedValues.length) {
            values.push(item);
        }

        // for (let i = 0; i < values.length; i++) {
        //     switch (values) {
        //         case (K11):
        //
        //             break;
        //         case (K112):
        //
        //             break;
        //         case (K121 || K122 || K123):
        //
        //             break;
        //         case (K211 || K212):
        //
        //             break;
        //         case (K221):
        //
        //             break;
        //         case (K231 || K232 || K233 || K234 || K235):
        //
        //             break;
        //     }
        // }

        this.setState({
            selectedValues : values
        })
    }




    render() {
        const {values, values_text} = this.props.item;

        // categories for different hospitals
        // University: K111
        // Allgemeinspital, Zentrumversorgung: K112
        // Allgemeinspital, Grundversorgung: K121, K122, K123
        // Psychiatrische Klinik: K211, K212
        // Rehabilitationsklinik: K221
        // Spezialklinik: K231, K232, K233, K234, K235

        let categorizedHospitalTypes = {}
        categorizedHospitalTypes["Uni"] = "Universitätsspital";
        categorizedHospitalTypes["AlgZ"] = "Allgemeinspital, Zentrumversorgung";
        categorizedHospitalTypes["AlgG"] = "Allgemeinspital, Grundversorgung";
        categorizedHospitalTypes["PsyK"] = "Psychiatrische Klinik";
        categorizedHospitalTypes["Reha"] = "Rehabilitationsklinik";
        categorizedHospitalTypes["Spez"] = "Spezialklinik";

        console.log(categorizedHospitalTypes);

        return (
            <CheckboxList items={categorizedHospitalTypes} checkboxSelectItem={this.checkboxSelectItem} mappingObject={categorizedHospitalTypes}/>
        )
    }
}

export default HospitalTypeFilter;
