import React, { Component } from 'react';
import CheckboxList from '../../CheckboxList/CheckboxList.js';

class HospitalTypeFilter extends Component {

    state = {
        mappingObject : {},
        selectedValues : []
    }


    checkboxSelectItem = (item) => {
        // removes item if in selectedValues
        let values = this.state.selectedValues.filter((value) => {
            return (value !== item)
        });

        // adds item if not in selectedValues
        if (values.length === this.state.selectedValues.length) {
            values.push(item);
        }

        this.setState({
            selectedValues : values
        })
    }




    render() {
        console.log(this.props);
        console.log(this.props.item.text);
        let mapObj = {};
        for (let i = 0; i < this.props.item.length; i++)
			mapObj[this.props.values[i]] = this.props.values_text[i];

        return (
            <CheckboxList items={this.props.item.values} checkboxSelectItem={this.checkboxSelectItem} mappingObject={mapObj}/>
        )
    }
}

export default HospitalTypeFilter;
